import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";



class App extends React.Component{
constructor(props){
  super(props)
  this.state ={
    orders: [],
    items: [
      {
        id: 1,
        title: 'Свитер серый',
        img: 'sweet.jpeg',
        desc: 'Свитер вязаный оверсайз - идеальный вариант для любого роста. ',
        category: 'Свитер',
        price: '29.99'
        },
        {
          id: 2,
          title: 'Брюки бежевые',
          img: 'bruk.jpg',
          desc: 'Брюки широкие – идеальное сочетание делового стиля и модных тенденций. ',
          category: 'Штаны',
          price: '39.99'
          },
          {
            id: 3,
            title: 'Рубашка белая',
            img: 'rub.jpeg',
            desc: 'Рубашка оверсайз  - идеальное сочетание комфорта и классического стиля. ',
            category: 'Рубашки',
            price: '34.99'
            }, 
            {
              id: 4,
              title: 'Худи серая',
              img: 'hudi.jpeg',
              desc: 'Худи оверсайз  - идеальный вариант для повседневной носки и комфорта. ',
              category: 'Толстовка',
              price: '30.99'
              }
    ]
  }
  this.addToOrder=this.addToOrder.bind(this)
}
  render() {
  return(
  <div className="wrapper">
    <Header orders={this.state.orders}/>
    <Items items={this.state.items} onAdd={this.addToOrder}/>
    <Footer />
  </div>
)
}

addToOrder(item) {
this.setState({orders: [...this.state.orders, item]})
}
}
 export default App;
