import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";
import ShowFullItem from "./components/ShowFullItem";



class App extends React.Component{
constructor(props){
  super(props)
  this.state ={
    orders: [],
    currentItems: [],
    items: [
      {
        id: 1,
        title: 'Свитер серый',
        img: 'sweet.jpeg',
        desc: 'Свитер вязаный оверсайз - идеальный вариант для любого роста. ',
        category: 'Свитер',
        price: '29.99'
        },
        {
          id: 2,
          title: 'Брюки бежевые',
          img: 'bruk.jpg',
          desc: 'Брюки широкие – идеальное сочетание делового стиля и модных тенденций. ',
          category: 'Штаны',
          price: '39.99'
          },
          {
            id: 3,
            title: 'Рубашка белая',
            img: 'rub.jpeg',
            desc: 'Рубашка оверсайз  - идеальное сочетание комфорта и классического стиля. ',
            category: 'Рубашки',
            price: '34.99'
            }, 
            {
              id: 4,
              title: 'Худи серая',
              img: 'hudi.jpeg',
              desc: 'Худи оверсайз  - идеальный вариант для повседневной носки и комфорта. ',
              category: 'Толстовка',
              price: '30.99'
              }
    ],
    showFullItem: false
  }
  this.state.currentItems = this.state.items
  this.addToOrder=this.addToOrder.bind(this)
  this.deleteOrder=this.deleteOrder.bind(this)
  this.chooseCategory=this.chooseCategory.bind(this)
  this.onShowItem=this.onShowItem.bind(this)
}
  render() {
  return(
  <div className="wrapper">
    <Header orders={this.state.orders} onDelete ={this.deleteOrder}/>
    <Categories chooseCategory={this.chooseCategory}/>
    <Items onShowItem = {this.onShowItem} items={this.state.currentItems} onAdd={this.addToOrder}/>

    {this.state.showFullItem && <ShowFullItem />}
    <Footer />
  </div>
)
}

onShowItem() {
  this.setState({showFullItem: !this.state.showFullItem})
}


chooseCategory(category) {
  if(category == 'all') {
    this.setState({currentItems: this.state.items})
    return
  }

  this.setState({
    currentItems: this.state.items.filter(el => el.category === category)
  })
}

deleteOrder(id) {
this.setState({orders: this.state.orders.filter(el => el.id !== id)})
}

addToOrder(item) {

let isInArray = false
  this.state.orders.forEach(el => {
  if(el.id === item.id)
   isInArray = true
})
if (!isInArray)
  this.setState({orders: [...this.state.orders, item]})
}
}
 export default App;
